javascript:(function(){const elements=document.querySelectorAll("input, select, textarea");const resultsValid=[];const resultsInvalidOrMissing=[];const totalFormElements=elements.length;const allowedValues=["on","off","name","given-name","additional-name","family-name","email","tel","url","street-address","address-line1","address-line2","address-line3","address-level1","address-level2","address-level3","address-level4","country","country-name","postal-code","cc-name","cc-given-name","cc-additional-name","cc-family-name","cc-number","cc-exp","cc-exp-month","cc-exp-year","cc-csc","cc-type","transaction-currency","transaction-amount","bday","bday-day","bday-month","bday-year","sex","gender","organization","organization-title","language","username","new-password","current-password","one-time-code","tel-country-code","tel-national","tel-area-code","tel-local","tel-extension","impp","photo","shipping-name","shipping-given-name","shipping-family-name","shipping-street-address","shipping-postal-code","shipping-country","billing-name","billing-given-name","billing-family-name","billing-street-address","billing-postal-code","billing-country","home-email","work-email","mobile-tel","home-tel","work-tel","fax-tel","pager-tel"];elements.forEach((function(element){if(element.hasAttribute("autocomplete")){let autocompleteValue=element.getAttribute("autocomplete");let isValid=allowedValues.includes(autocompleteValue)?"valid":"invalid";resultsValid.push({element:element,autocomplete:autocompleteValue,valid:isValid})}else{resultsInvalidOrMissing.push({element:element,autocomplete:"[null]",valid:"[missing]"})}}));alert(`${totalFormElements} form elements detected.\n${resultsValid.length} elements with autocomplete attribute.`);elements.forEach((function(element){const label=document.createElement("p");label.style.cssText="background:#ff0;color:#000;display:block;max-inline-size:fit-content;padding:2px 5px;margin-top:2px;border-radius:3px;font-size:15px;font-family:monospace;font-weight:bold";if(element.hasAttribute("autocomplete")){const value=element.getAttribute("autocomplete");const isValid=allowedValues.includes(value)?'<span role="status">Valid value</span>':'<span role="alert">Invalid value</span>';label.innerHTML=`autocomplete="${value}" ${isValid}`;label.style.backgroundColor=allowedValues.includes(value)?"#cfc":"#fcc"}else{label.textContent="autocomplete missing";label.style.backgroundColor="#ffc"}element.parentNode.insertBefore(label,element.nextSibling)}))})();